/// image preview·support svg

import { useState, useMemo, useEffect, useRef } from 'react';
import axios, { type AxiosResponse } from 'axios';

import * as classes from './index.module.scss';

interface Props {
    width?: string, // 图片宽度
    height?: string, // 图片高度
    alt?: string,  // img.alt属性
    src: string, // 图片链接
    fallback?: string // 加载失败容错地址
}

export default ({
    width ='100%',
    height = '100%',
    src,
    alt,
    // eslint-disable-next-line max-len
    fallback = 'data:image/png;base64,'
}: Props) => {

    const [loading, setLoading] = useState(false); // 图片加载loading
    const [isValid, setIsValid] = useState(false); // 链接是否有效

    const [svgContentStr, setSvgContentStr] = useState(''); // svg字符串内容
    const svgParentContainer = useRef<HTMLElement>(document.body); // svg元素的父元素

    const imageLink = useMemo(() => isValid ? src : fallback, [isValid, src, fallback]); // 真实显示出来的图片地址
    const isSvg = useMemo(() => /(\.svg)$/igm.test(imageLink), [imageLink]); // 是否是svg格式的图片

    const fetchSvg = (address: string): Promise<void> => { // 请求svg图像
        setLoading(true);
        return axios.get(address).then(({ data }: AxiosResponse<string, string>) => {
            setSvgContentStr(data), // 保存svg内容
            setIsValid(true) // 更改图片链接有效状态

            useEffect(() => {
                const svgEl = svgParentContainer.current.querySelector('svg');
                if (svgEl !== null) {
                    const svgRect: DOMRect = svgEl.getBBox();
                    svgEl.setAttribute('viewBox', `${svgRect.x} ${svgRect.y} ${svgRect.width} ${svgRect.height}`); // 让svg自适应展示
                }
            }, []);
        }).catch(() => {
            setSvgContentStr(''), // 保存svg内容
            setIsValid(false) // 更改图片链接有效状态
        }).finally(() => {
            setLoading(false);
        });
    };

    useEffect(() => { // 当src链接发生变化时，重置链接有效状态
        setIsValid(true);
    }, [src]);

    useEffect(() => {
        if (isSvg) fetchSvg(imageLink); // 如果是svg就加载svg图片
    }, [imageLink]);

    return (
        isSvg ? ( // svg container
            <div
                className={classes.imagePreview}
                dangerouslySetInnerHTML={{__html: svgContentStr}}
                style={{ width: width, height: height }}
                ref={svgParentContainer as any}
            />
        ) : ( // image container
            <img
                className={classes.imagePreview}
                src={imageLink}
                alt={alt}
                onError={() => setIsValid(false)}
                onLoad={() => setLoading(false)}
                loading="lazy"
                onLoadStart={() => setLoading(true)}
                style={{ width, height }}
            />
        )
    )
}